// }

const App = () => {
  const [data, setData] = useState(null);
  const [city, setCity] = useState(null);
  const [isAuth, setAuth] = useState(false);
  const [test, setTest] = useState([null]);


  const handleClick = (event) => {
    event.preventDefault();

    fetch("http://127.0.0.1:8000/api/test/", {method: 'GET'})
    .then(response => response.json())
    .then(json => setTest(json))
    .catch(errors => console.error(errors));

  }


  // useEffect(() => {
  //   fetch(url)
  //   .then(response => response.json())
  //   .then(json => setData(json))
  //   .catch(errors => console.error(errors))
  // }, []);

  const handleChange = (event) => {
    const newCity = event.target.value;
    setCity(newCity);
  }

  const handleSubmit = (event) => {
    const url = `http://api.weatherapi.com/v1/current.json?key=e77503e6c87a4abf9eb203542242908&q=${city}`;

    event.preventDefault();
    console.log("Submitted!");

    fetch(url)
    .then(response => response.json())
    .then(json => setData(json))
    .catch(errors => console.error(errors));

    setCity(null);
  }


  if (isAuth) {
    return (
      <LogIn setAuth={setAuth}></LogIn>
    );
  }

  return (
    <div>
      <InputBox handleChange={handleChange} handleSubmit={handleSubmit} city={city}></InputBox>
      <Weather data={data} /> 
      <Weather data={data} /> 
      <Weather data={data} />
      <button type='button' onClick={handleClick}>Button</button>
      <p>Test: {{test}}</p>
    </div>    
  );
}

export default App;
