import './App.css';
import React, { useState, useEffect } from 'react';

const App = () => {
  const [data, setData] = useState(null);
  const [city, setCity] = useState(null);


  // useEffect(() => {
  //   fetch(url)
  //   .then(response => response.json())
  //   .then(json => setData(json))
  //   .catch(errors => console.error(errors))
  // }, []);

  const handleChange = (event) => {
    const newCity = event.target.value;
    setCity(newCity);
  }

  const url = "http://api.weatherapi.com/v1/current.json?key=e77503e6c87a4abf9eb203542242908&q=Riverside";
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("Submitted!");
    setCity(null);

    fetch(url)
    .then(response => response.json())
    .then(json => setData(json))
    .catch(errors => console.error(errors));

    if (!data) {
      return <p>Oops something went wrong!</p>;
    }
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>Enter city name:</label>
        <input type="text" value={city || ""} name="cityName" onChange={handleChange}></input>
        <button type="submit">submit</button>
      </form>

      <p>Current temp: <pre>{data.current.temp_f}</pre></p>
    </div>
  );
}

export default App;
