import './App.css';
import React, { useState, useEffect } from 'react';


const Test = () => {
  const [test, setTest] = useState(null);

  const handleClick = (event) => {
    event.preventDefault();

    fetch("http://127.0.0.1:8000/core/register/", (method: 'POST'))
    .then(response => response.json())
    .then(json => setTest(json))
    .catch(errors => console.error(errors));
  }

  return (
    <div>
    <p>Test data: {test}</p>
    <button onClick={handleClick}>Test Button</button>
    </div>
  );
}

const App = () => {
  const [data, setData] = useState(null);
  const [city, setCity] = useState(null);

  // useEffect(() => {
  //   fetch(url)
  //   .then(response => response.json())
  //   .then(json => setData(json))
  //   .catch(errors => console.error(errors))
  // }, []);

  const handleChange = (event) => {
    const newCity = event.target.value;
    setCity(newCity);
  }

  const handleSubmit = (event) => {
    const url = `http://api.weatherapi.com/v1/current.json?key=e77503e6c87a4abf9eb203542242908&q=${city}`;

    event.preventDefault();
    console.log("Submitted!");

    fetch(url)
    .then(response => response.json())
    .then(json => setData(json))
    .catch(errors => console.error(errors));

    setCity(null);
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>Enter city name:</label>
        <input type="text" value={city || ""} name="cityName" onChange={handleChange}></input>
        <button type="submit">submit</button>
      </form>

      {data&& 
        <div>
        <p>Current city: {data.location.name}</p>
        <p>Current state: {data.location.region}</p>
        <p>Current country: {data.location.country}</p>
        <p>Current temp: {data.current.temp_f}</p>
        <p>Current condition: {data.current.condition.text}</p>
        <p>Current wind: {data.current.wind_mph}</p>
        <img src={data.current.condition.icon} alt='img of conditions'></img>
        </div>
      }
      <Test />
    </div>
  );
}

export default App;
